// Modal.jsx
export default class Modal extends React.Component {
  componentWillMount() {
    this.modalRoot = document.getElementById('modal-root');
  }

  render() {
    const { show, children, backDropClick } = this.props;
    if (show) {
      return ReactDOM.createPortal((
        <div className="backdrop" onClick={backDropClick}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            {children}
          </div>
        </div>
      ), this.modalRoot);
    } else {
      return null;
    }
  }
}

.backdrop {
  position: fixed;
  top: 0px;
  bottom: 0px;
  left: 0px;
  right: 0px;
  background-color: rgba(0,0,0,0.5);
  padding: 16px;
  z-index: 250;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* SomeComponent.jsx */
export default class SomeComponent extends React.Component {
  constructor(props) {
    super(props);
    this.openModal = this.openModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.doSomething = this.doSomething.bind(this);
    this.state = {
      modal: false,
    };
  }

  openModal() {
    this.setState({ modal: true });
  }

  closeModal() {
    this.setState({ modal: false });
  }

  doSomething() {
    alert('You did something!');
  }

  render() {
    return (
      <Fragment>
        <button onClick={this.openModal}>Do something</button>
        <Modal show={this.state.modal} backDropClick={this.closeModal}>
          <Card title="Modal">
            <CardContent>
              <p>Are you sure you want to do something?</p>
            </CardContent>
            <CardActions>
              <CardAction label="Close" onClick={this.closeModal} />
              <CardAction label="Confirm" onClick={this.doSomething} />
            </CardActions>
          </Card>
        </Modal>
      </Fragment>
    );
  }
}
